DROP DATABASE IF EXISTS RecipeProject;
CREATE DATABASE RecipeProject;

USE RecipeProject;

# adminDB
create table adminDB(No int primary key auto_increment, id varchar(10), pw varchar(10));

# userDB
create table userDB(No int primary key auto_increment, id varchar(10), pw varchar(10), name varchar(20));

#recipeDB
create table recipeDB(No int primary key auto_increment, name varchar(10), made varchar(300), nutrient varchar(100), time int, ingredient varchar(100), kcal FLOAT, category VARCHAR(10));
	
	
				
	

	
	
				
				
				
				
				
				
				
				
				
				
				
				
				


